          {character.status && <h2>Status: {character.status}</h2>}
          {character.species && <h2>Specie: {character.species}</h2>}
          {character.gender2 && <h2>Gender: {character.gender}</h2>}
          {character.origin?.name && character.origin.name === "unknown" ? (
